mapper.select:
select DISTINCT f.dong
from member m join favorite f 
on f.id= #{id}

SELECT DISTINCT h.no, h.dong, AptName, code, dealAmount, buildYear, dealYear, dealMonth, dealDay, area, floor, jibun 
FROM favorite f JOIN housedeal h
WHERE h.dong = #{dong}

SELECT id, no, dong 
FROM favorite 
WHERE id = #{id}

mapper.update:
update favorite 
set dong = #{dong}
where id =  #{id}
