mapper.insert:
insert into member(
	id, pwd, dpt, email
) values (
	#{id}, #{pwd}, #{dpt}, #{email}
)

mapper.select:
select id, pwd, dpt, email
  from member
 where id = #{id}

mapper.update:
update member
   set pwd = #{pwd}, 
	   dpt = #{dpt}, 
	   email = #{email}
 where id = #{id}

mapper.select:
select id, email
from member
where id = #{id} and pwd = #{pwd}
