mapper.insert:
insert into member(
	id, pwd, dpt, email, authkey
) values (
	#{id}, #{pwd}, #{dpt}, #{email}, #{authkey}
)

mapper.select:
select id, pwd, dpt, email, authkey
  from member
 where id = #{id}

select id, pwd, dpt, email, authkey
  from member
 where email = #{email}

mapper.update:
update member
   set pwd = #{pwd}, 
	   dpt = #{dpt}, 
	   email = #{email},
	   authkey = #{authkey}
 where id = #{id}

mapper.select:
select id, email
from member
where id = #{id} and pwd = #{pwd}
